SELECT COUNT(*) as agg_0, MAX("nesreca"."x_wgs84" + "nesreca"."y") as agg_1 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" NOT LIKE '%o%b%' AND "nesreca"."tip_nesreca" != 'PZ' AND "oseba"."alkotest_d" != 'C'