SELECT SUM("upravna_enota"."st_prebivalcev") as agg_0, AVG("upravna_enota"."povrsina" + "upravna_enota"."st_prebivalcev") as agg_1, MIN("upravna_enota"."st_prebivalcev" + "upravna_enota"."id_upravna_enota") as agg_2 FROM "upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" IN ('Brežice', 'Ormož', 'Piran', 'Ravne na Koroškem', 'Ptuj', 'Radovljica', 'Ajdovščina', 'Postojna', 'Novo Mesto') AND "upravna_enota"."ime_upravna_enota" NOT LIKE '%p%ri%' AND "upravna_enota"."povrsina" = 453 AND ("upravna_enota"."id_upravna_enota" = 5550 OR "upravna_enota"."st_prebivalcev" >= 23886 OR "upravna_enota"."id_upravna_enota" = 5510)