SELECT MIN("upravna_enota"."id_upravna_enota" + "nesreca"."y") as agg_0, AVG("nesreca"."y") as agg_1, MAX("nesreca"."x_wgs84" + "oseba"."starost") as agg_2 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "nesreca"."oznaka_odsek_ali_ulica" LIKE '%00000%' AND "oseba"."vrsta_udelezenca" NOT LIKE '%PT%'