SELECT MIN("nesreca"."upravna_enota" + "nesreca"."y_wgs84") as agg_0, AVG("upravna_enota"."id_upravna_enota" + "oseba"."strokovni_pregled") as agg_1 FROM "nesreca" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."st_prebivalcev" BETWEEN 15539 AND 31473