SELECT COUNT(*) as agg_0, MIN("oseba"."drzavljanstvo") as agg_1, SUM("nesreca"."y") as agg_2 FROM "oseba" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca"  WHERE "upravna_enota"."ime_upravna_enota" NOT LIKE '%ob%' AND "nesreca"."vzrok_nesreca" = 'VO'