SELECT MAX("oseba"."strokovni_pregled" + "oseba"."vozniski_staz_LL") as agg_0, MAX("oseba"."drzavljanstvo") as agg_1 FROM "nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "oseba" ON "upravna_enota"."id_upravna_enota" = "oseba"."upravna_enota"  WHERE "upravna_enota"."povrsina" = 128 AND "upravna_enota"."ime_upravna_enota" IN ('Ormo≈æ', 'Novo Mesto') AND "oseba"."vrsta_udelezenca" NOT LIKE '%AV%' AND "oseba"."upravna_enota" = 5599