SELECT MIN("oseba"."spol" + "oseba"."vozniski_staz_MM") as agg_0, MIN("oseba"."upravna_enota" + "oseba"."vozniski_staz_MM") as agg_1 FROM "nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca"  WHERE "upravna_enota"."ime_upravna_enota" NOT LIKE '%B%istrica%' AND "oseba"."starost" BETWEEN 34.55948120717399 AND 34.85833751309309