SELECT MAX("nesreca"."x") as agg_0, MIN("nesreca"."y" + "oseba"."alkotest") as agg_1, MIN("oseba"."vozniski_staz_MM") as agg_2 FROM "upravna_enota" LEFT OUTER JOIN "oseba" ON "upravna_enota"."id_upravna_enota" = "oseba"."upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" != 'Grosuplje' AND "oseba"."upravna_enota" = 5542 AND "upravna_enota"."ime_upravna_enota" LIKE '%o%b%' AND "nesreca"."tekst_odsek_ali_ulica" = 'JAVORNIŠKO NABREŽJE'