SELECT AVG("oseba"."vozniski_staz_LL" + "oseba"."strokovni_pregled") as agg_0, AVG("nesreca"."upravna_enota") as agg_1, SUM("oseba"."strokovni_pregled" + "upravna_enota"."st_prebivalcev") as agg_2 FROM "nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "oseba" ON "upravna_enota"."id_upravna_enota" = "oseba"."upravna_enota"  WHERE "nesreca"."oznaka_odsek_ali_ulica" = '1257.0'