SELECT SUM("oseba"."vozniski_staz_MM" + "oseba"."spol") as agg_0 FROM "nesreca" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE ("upravna_enota"."povrsina" >= 456 OR "upravna_enota"."ime_upravna_enota" = 'Slovenska Bistrica') AND "oseba"."starost_d" IN ('E', 'F', 'C', 'G', 'A', 'N') AND ("oseba"."vozniski_staz_d" IN ('I', 'F', 'A', 'E', 'G', 'B') OR ("oseba"."vozniski_staz_d" IN ('N', 'A') AND "oseba"."vozniski_staz_d" != 'C'))