SELECT MAX("oseba"."id_nesreca") as agg_0, MIN("nesreca"."upravna_enota" + "nesreca"."stacionazna_ali_hisna_st") as agg_1 FROM "nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca"  WHERE "upravna_enota"."povrsina" <= 251 AND "upravna_enota"."st_prebivalcev" BETWEEN 16169 AND 21124