SELECT MAX("oseba"."starost" + "oseba"."upravna_enota") as agg_0, AVG("nesreca"."stacionazna_ali_hisna_st" + "nesreca"."upravna_enota") as agg_1 FROM "nesreca" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca"  WHERE ("oseba"."strokovni_pregled_d" IN ('E', 'B', 'I', 'D', 'G', 'A', 'F', 'N', 'H') OR "oseba"."strokovni_pregled_d" = 'A') AND "nesreca"."stanje_vozisce" LIKE '%SP%' AND "nesreca"."y" >= 148166.01877708707