SELECT MAX("oseba"."vozniski_staz_MM") as agg_0, MAX("oseba"."vozniski_staz_MM" + "oseba"."vozniski_staz_LL") as agg_1, MAX("oseba"."drzavljanstvo" + "upravna_enota"."id_upravna_enota") as agg_2 FROM "oseba" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca"  WHERE ("upravna_enota"."id_upravna_enota" <= 5536 OR ("upravna_enota"."id_upravna_enota" BETWEEN 5515 AND 5528 AND "upravna_enota"."id_upravna_enota" >= 5519)) AND "nesreca"."oznaka_odsek_ali_ulica" NOT LIKE '%00000%'