SELECT SUM("upravna_enota"."id_upravna_enota") as agg_0, AVG("upravna_enota"."povrsina" + "nesreca"."x") as agg_1 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."id_upravna_enota" <= 5508 AND "oseba"."strokovni_pregled" >= 0.0