SELECT MIN("nesreca"."x" + "nesreca"."id_nesreca") as agg_0, MAX("oseba"."strokovni_pregled") as agg_1 FROM "nesreca" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" NOT LIKE '%Bistrica%' AND "nesreca"."naselje_ali_izven" = 'N'