SELECT SUM("nesreca"."id_nesreca" + "oseba"."id_nesreca") as agg_0, SUM("oseba"."drzavljanstvo" + "oseba"."spol") as agg_1, SUM("oseba"."upravna_enota" + "nesreca"."upravna_enota") as agg_2 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca"  WHERE "oseba"."vozniski_staz_LL" BETWEEN 0.049956845601632716 AND 1.7175464617342142 AND "oseba"."strokovni_pregled_d" IN ('H', 'I', 'A', 'N', 'C', 'F') AND "oseba"."alkotest_d" = 'F'