SELECT AVG("nesreca"."y") as agg_0, COUNT(*) as agg_1, COUNT(*) as agg_2 FROM "nesreca" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "nesreca"."tekst_odsek_ali_ulica" LIKE '%ODS%EKOV%' AND ("upravna_enota"."ime_upravna_enota" = 'Kr≈°ko' OR "upravna_enota"."ime_upravna_enota" LIKE '%Bistrica%')