SELECT MAX("nesreca"."upravna_enota") as agg_0, MAX("nesreca"."y_wgs84" + "nesreca"."id_nesreca") as agg_1 FROM "nesreca" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca"  WHERE "nesreca"."y" BETWEEN 78446.42200522273 AND 167774.63639607973 AND "oseba"."varnostni_pas_ali_celada" != '2.0' AND "nesreca"."stanje_vozisce" IN ('OS', 'SP', 'MO', 'SU', 'SN', 'SL', 'PN')