SELECT MIN("nesreca"."x" + "oseba"."vozniski_staz_MM") as agg_0, MAX("oseba"."vozniski_staz_MM" + "upravna_enota"."st_prebivalcev") as agg_1 FROM "upravna_enota" LEFT OUTER JOIN "oseba" ON "upravna_enota"."id_upravna_enota" = "oseba"."upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota"  WHERE "upravna_enota"."st_prebivalcev" >= 84604 AND "upravna_enota"."ime_upravna_enota" LIKE '%pr%i%' AND "oseba"."alkotest_d" = 'G'