SELECT MAX("nesreca"."y") as agg_0, MIN("nesreca"."x" + "nesreca"."stacionazna_ali_hisna_st") as agg_1, AVG("nesreca"."y" + "oseba"."vozniski_staz_LL") as agg_2 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE ("oseba"."vrsta_udelezenca" = 'SP' OR "oseba"."vrsta_udelezenca" = 'SK') AND "oseba"."povzrocitelj_ali_udelezenec" IN ('D') AND "oseba"."alkotest_d" = 'G' AND ("upravna_enota"."st_prebivalcev" != 13980 OR ("upravna_enota"."st_prebivalcev" <= 12553 AND "upravna_enota"."st_prebivalcev" <= 51798))