SELECT MIN("upravna_enota"."povrsina") as agg_0, COUNT(*) as agg_1 FROM "upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca"  WHERE "oseba"."starost_d" != 'C' AND "upravna_enota"."ime_upravna_enota" != 'Litija' AND "upravna_enota"."id_upravna_enota" >= 5593 AND "upravna_enota"."st_prebivalcev" <= 18118