SELECT AVG("upravna_enota"."povrsina") as agg_0, AVG("oseba"."alkotest") as agg_1, MAX("upravna_enota"."st_prebivalcev" + "oseba"."alkotest") as agg_2 FROM "oseba" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."id_upravna_enota" != 5519 AND ("upravna_enota"."ime_upravna_enota" != 'Ribnica' OR "upravna_enota"."povrsina" BETWEEN 189 AND 268 OR "upravna_enota"."ime_upravna_enota" LIKE '%o%b%')