SELECT AVG("upravna_enota"."povrsina" + "upravna_enota"."id_upravna_enota") as agg_0, MAX("upravna_enota"."id_upravna_enota") as agg_1, SUM("upravna_enota"."st_prebivalcev" + "upravna_enota"."povrsina") as agg_2 FROM "upravna_enota"  WHERE "upravna_enota"."povrsina" != 872 AND "upravna_enota"."ime_upravna_enota" IN ('Piran', 'Ravne na Koroškem', 'Ormož', 'Novo Mesto', 'Brežice', 'Postojna', 'Ajdovščina', 'Radlje ob Dravi', 'Radovljica') AND "upravna_enota"."ime_upravna_enota" NOT LIKE '%Lj%ubljana%' AND "upravna_enota"."st_prebivalcev" <= 27182