SELECT MIN("oseba"."drzavljanstvo") as agg_0, MIN("upravna_enota"."povrsina") as agg_1 FROM "nesreca" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" = 'Slovenske Konjice' AND ("oseba"."vozniski_staz_MM" <= 3.3613056582199246 OR ("oseba"."vozniski_staz_MM" >= 0.0 AND "oseba"."vozniski_staz_MM" BETWEEN 6.805338940276203 AND 10.450327178904645)) AND "nesreca"."klas_nesreca" IN ('B', 'H', 'S') AND "nesreca"."tip_nesreca" NOT LIKE '%OP%' AND "oseba"."upravna_enota" <= 5599