SELECT MAX("upravna_enota"."id_upravna_enota") as agg_0, AVG("nesreca"."upravna_enota") as agg_1, SUM("upravna_enota"."povrsina" + "nesreca"."stacionazna_ali_hisna_st") as agg_2 FROM "upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota"  WHERE ("nesreca"."oznaka_cesta_ali_naselje" NOT LIKE '%010-1%' OR "nesreca"."oznaka_cesta_ali_naselje" LIKE '%03011%' OR "nesreca"."oznaka_cesta_ali_naselje" IS NOT NULL) AND "upravna_enota"."ime_upravna_enota" LIKE '%Ljubljana%' AND "upravna_enota"."ime_upravna_enota" IN ('Postojna') AND "nesreca"."naselje_ali_izven" = 'D'