SELECT MIN("oseba"."vozniski_staz_MM") as agg_0, MAX("nesreca"."id_nesreca" + "nesreca"."y") as agg_1 FROM "nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca"  WHERE "oseba"."alkotest" BETWEEN 0.0 AND 1.0 AND "oseba"."strokovni_pregled" <= 0.0 AND "upravna_enota"."ime_upravna_enota" != 'Idrija'