SELECT AVG("upravna_enota"."povrsina") as agg_0, MIN("nesreca"."upravna_enota") as agg_1 FROM "upravna_enota" LEFT OUTER JOIN "oseba" ON "upravna_enota"."id_upravna_enota" = "oseba"."upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota"  WHERE "oseba"."spol" <= 1 AND ("oseba"."vozniski_staz_LL" IS NOT NULL OR "oseba"."vozniski_staz_LL" BETWEEN 26.576364054969464 AND 28.744742872546443) AND "oseba"."starost" >= 44.779277823038946 AND "nesreca"."y_wgs84" <= 46.300973512106125