SELECT MIN("upravna_enota"."st_prebivalcev") as agg_0, AVG("oseba"."vozniski_staz_LL" + "upravna_enota"."id_upravna_enota") as agg_1 FROM "nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca"  WHERE "upravna_enota"."ime_upravna_enota" LIKE '%Bistrica%' AND "oseba"."starost_d" IN ('E', 'I', 'B', 'F', 'H') AND "oseba"."strokovni_pregled_d" != 'J'