SELECT MIN("upravna_enota"."st_prebivalcev") as agg_0, COUNT(*) as agg_1 FROM "upravna_enota" LEFT OUTER JOIN "oseba" ON "upravna_enota"."id_upravna_enota" = "oseba"."upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" LIKE '%B%istrica%' AND "nesreca"."tekst_cesta_ali_naselje" = 'RADOBLJE - PLAZOVJE'