SELECT SUM("nesreca"."id_nesreca") as agg_0 FROM "nesreca" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" = 'Jesenice' AND "upravna_enota"."st_prebivalcev" >= 19438 AND "upravna_enota"."povrsina" >= 276 AND "oseba"."starost_d" != 'A' AND "nesreca"."oznaka_cesta_ali_naselje" NOT LIKE '%25001%'