SELECT MIN("oseba"."drzavljanstvo") as agg_0 FROM "oseba" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "oseba"."vrsta_udelezenca" IN ('MK', 'AV') AND "upravna_enota"."st_prebivalcev" BETWEEN 10493 AND 18185 AND "upravna_enota"."ime_upravna_enota" IN ('Ravne na Koroškem', 'Ajdovščina', 'Radlje ob Dravi', 'Radovljica', 'Ptuj', 'Ormož')