SELECT COUNT(*) as agg_0, MIN("oseba"."strokovni_pregled" + "nesreca"."x_wgs84") as agg_1, MIN("oseba"."upravna_enota" + "oseba"."id_nesreca") as agg_2 FROM "upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca"  WHERE ("upravna_enota"."ime_upravna_enota" LIKE '%ob%' OR ("upravna_enota"."st_prebivalcev" BETWEEN 3332 AND 9789 AND "upravna_enota"."ime_upravna_enota" IN ('Brežice', 'Ajdovščina', 'Ravne na Koroškem'))) AND "nesreca"."vzrok_nesreca" NOT LIKE '%PV%'