SELECT AVG("upravna_enota"."st_prebivalcev" + "nesreca"."x_wgs84") as agg_0 FROM "upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota" LEFT OUTER JOIN "oseba" ON "upravna_enota"."id_upravna_enota" = "oseba"."upravna_enota"  WHERE "oseba"."vrsta_udelezenca" LIKE '%KM%' AND "nesreca"."upravna_enota" <= 5528 AND "nesreca"."oznaka_odsek_ali_ulica" NOT LIKE '%00000%' AND "upravna_enota"."ime_upravna_enota" LIKE '%o%b%' AND "oseba"."strokovni_pregled" BETWEEN 0.0 AND 1.0