SELECT AVG("oseba"."vozniski_staz_MM" + "oseba"."alkotest") as agg_0, MIN("nesreca"."x" + "upravna_enota"."id_upravna_enota") as agg_1 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "nesreca"."tip_nesreca" IN ('OS', 'BT', 'PP', 'PR') AND "upravna_enota"."ime_upravna_enota" LIKE '%Bis%trica%'