SELECT AVG("upravna_enota"."povrsina") as agg_0, MIN("upravna_enota"."id_upravna_enota") as agg_1 FROM "upravna_enota" LEFT OUTER JOIN "oseba" ON "upravna_enota"."id_upravna_enota" = "oseba"."upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" IN ('Radlje ob Dravi', 'Ravne na Koroškem') AND "oseba"."povzrocitelj_ali_udelezenec" IN ('N') AND "upravna_enota"."st_prebivalcev" <= 16976 AND "upravna_enota"."ime_upravna_enota" IN ('Radlje ob Dravi', 'Ptuj', 'Novo Mesto', 'Ravne na Koroškem', 'Ajdovščina', 'Radovljica', 'Piran', 'Ormož', 'Postojna')