SELECT AVG("upravna_enota"."povrsina" + "oseba"."upravna_enota") as agg_0, MIN("oseba"."id_nesreca") as agg_1 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "nesreca"."stanje_vozisce" = 'SN' AND "nesreca"."tip_nesreca" NOT LIKE '%B%T%'