SELECT SUM("upravna_enota"."id_upravna_enota") as agg_0, MIN("oseba"."upravna_enota" + "oseba"."alkotest") as agg_1 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" IN ('Brežice', 'Novo Mesto', 'Piran', 'Radlje ob Dravi', 'Ravne na Koroškem', 'Postojna', 'Ajdovščina')