SELECT MAX("nesreca"."y_wgs84") as agg_0, SUM("oseba"."strokovni_pregled") as agg_1, SUM("oseba"."id_nesreca" + "upravna_enota"."id_upravna_enota") as agg_2 FROM "nesreca" LEFT OUTER JOIN "oseba" ON "nesreca"."id_nesreca" = "oseba"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" LIKE '%Bistrica%'