SELECT AVG("oseba"."starost") as agg_0, SUM("upravna_enota"."st_prebivalcev" + "upravna_enota"."povrsina") as agg_1, MAX("nesreca"."stacionazna_ali_hisna_st") as agg_2 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "nesreca"."stanje_vozisce" IN ('BL', 'SP', 'SN', 'SU', 'PN', 'SL', 'MO') AND "upravna_enota"."id_upravna_enota" <= 5524