SELECT AVG("upravna_enota"."povrsina") as agg_0, SUM("nesreca"."x_wgs84" + "nesreca"."stacionazna_ali_hisna_st") as agg_1 FROM "oseba" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" IN ('Radovljica')