SELECT MAX("nesreca"."upravna_enota") as agg_0, COUNT(*) as agg_1, SUM("upravna_enota"."st_prebivalcev" + "nesreca"."upravna_enota") as agg_2 FROM "upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota"  WHERE ("nesreca"."oznaka_cesta_ali_naselje" LIKE '%00003%' OR ("nesreca"."oznaka_cesta_ali_naselje" NOT LIKE '%1704%2%' AND "nesreca"."oznaka_cesta_ali_naselje" IN ('03011', '18035', '17042', '00001', '000A1', '64033', '00430', '25001'))) AND ("nesreca"."klas_nesreca" = 'U' OR ("nesreca"."vreme_nesreca" = 'N' AND "nesreca"."klas_nesreca" = 'L')) AND "upravna_enota"."ime_upravna_enota" LIKE '%ob%' AND "nesreca"."stanje_povrsina_vozisce" IN ('A', 'M')