SELECT SUM("nesreca"."y_wgs84") as agg_0, COUNT(*) as agg_1 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "upravna_enota"."ime_upravna_enota" NOT LIKE '%Ljubljana%' AND "oseba"."vozniski_staz_d" IN ('A', 'D') AND "nesreca"."opis_prizorisce" = 'C' AND ("upravna_enota"."ime_upravna_enota" IN ('Ormož', 'Brežice', 'Ravne na Koroškem', 'Ajdovščina', 'Radlje ob Dravi', 'Radovljica') OR "upravna_enota"."ime_upravna_enota" IN ('Radlje ob Dravi', 'Brežice', 'Piran', 'Ravne na Koroškem', 'Postojna', 'Ajdovščina') OR "upravna_enota"."ime_upravna_enota" IN ('Ajdovščina', 'Ravne na Koroškem', 'Radlje ob Dravi', 'Brežice', 'Novo Mesto', 'Ptuj', 'Radovljica', 'Piran', 'Ormož'))