SELECT COUNT(*) as agg_0, SUM("nesreca"."stacionazna_ali_hisna_st" + "nesreca"."x") as agg_1, COUNT(*) as agg_2 FROM "oseba" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota"  WHERE "oseba"."alkotest_d" = 'B' AND "upravna_enota"."ime_upravna_enota" NOT LIKE '%o%b%' AND ("nesreca"."tekst_cesta_ali_naselje" = '041020 CERK-D.VAS-ZELÅ E' OR "nesreca"."tekst_cesta_ali_naselje" IS NOT NULL)