SELECT MIN("oseba"."alkotest" + "nesreca"."id_nesreca") as agg_0 FROM "oseba" LEFT OUTER JOIN "upravna_enota" ON "oseba"."upravna_enota" = "upravna_enota"."id_upravna_enota" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca"  WHERE ("nesreca"."tekst_cesta_ali_naselje" IN ('KORENSKO SEDLO - OBREŽJE', 'ŠENTILJ-MB-K.DEKANI-KOPER', 'VIČ - HAJDINA (PTUJ)', 'KRANJ', 'CELJE', 'MARIBOR', 'PTUJ', 'ŠENTILJ - DEKANI') OR ("nesreca"."tekst_cesta_ali_naselje" = 'ROŠPOH-DEL-MARIBOR' AND "nesreca"."tekst_cesta_ali_naselje" IN ('ŠENTILJ - DEKANI', 'PTUJ', 'MARIBOR', 'ŠENTILJ-MB-K.DEKANI-KOPER', 'LJUBLJANA'))) AND "oseba"."strokovni_pregled_d" IN ('H', 'F', 'E', 'A', 'D', 'I')