SELECT MAX("nesreca"."stacionazna_ali_hisna_st") as agg_0 FROM "upravna_enota" LEFT OUTER JOIN "oseba" ON "upravna_enota"."id_upravna_enota" = "oseba"."upravna_enota" LEFT OUTER JOIN "nesreca" ON "upravna_enota"."id_upravna_enota" = "nesreca"."upravna_enota"  WHERE "oseba"."strokovni_pregled_d" = 'E' AND "upravna_enota"."ime_upravna_enota" IN ('Novo Mesto', 'Postojna', 'Brežice', 'Ravne na Koroškem', 'Piran', 'Ptuj', 'Radovljica') AND "oseba"."strokovni_pregled" >= 0.0