SELECT MAX("upravna_enota"."povrsina") as agg_0 FROM "oseba" LEFT OUTER JOIN "nesreca" ON "oseba"."id_nesreca" = "nesreca"."id_nesreca" LEFT OUTER JOIN "upravna_enota" ON "nesreca"."upravna_enota" = "upravna_enota"."id_upravna_enota"  WHERE "oseba"."poskodba" IN ('L', 'S', 'U', 'P', 'B') AND "oseba"."drzavljanstvo" >= 5 AND "oseba"."vozniski_staz_d" IN ('H', 'F', 'E', 'I', 'N', 'A', 'C')