SELECT AVG("On_Time_On_Time_Performance_2016_1"."Div1AirportSeqID") as agg_0, COUNT(*) as agg_1, SUM("On_Time_On_Time_Performance_2016_1"."DestAirportID" + "On_Time_On_Time_Performance_2016_1"."DestAirportSeqID") as agg_2 FROM "L_AIRLINE_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRLINE_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."AirlineID" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."OriginAirportID" = "L_AIRPORT_ID"."Code" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."OriginAirportSeqID" = "L_AIRPORT_SEQ_ID"."Code"  WHERE "On_Time_On_Time_Performance_2016_1"."ArrTimeBlk" NOT LIKE '%2000-2059%' AND "L_AIRPORT_ID"."Code" >= 281224 AND "L_AIRLINE_ID"."Description" LIKE '%d/b/a%'