SELECT SUM("On_Time_On_Time_Performance_2016_1"."ArrDelayMinutes") as agg_0 FROM "L_STATE_FIPS" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_STATE_FIPS"."Code" = "On_Time_On_Time_Performance_2016_1"."OriginStateFips" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."Div2AirportID" = "L_AIRPORT_ID"."Code"  WHERE "L_AIRPORT_ID"."Description" IN ('New Bight, The Bahamas: New Bight Airport', 'Three Forks, MT: Three Forks Airport', 'Ugashik, AK: Ugashik Airport', 'Barrow, AK: Ice Strip', 'Tarbes/Lourdes/Pyrenees, France: Tarbes-Lourdes-Pyrenees', 'Yuma, AZ: Yuma MCAS/Yuma International') AND "L_AIRPORT_ID"."Code" >= 129248 AND "On_Time_On_Time_Performance_2016_1"."Origin" LIKE '%SJ%U%'