SELECT AVG("On_Time_On_Time_Performance_2016_1"."Div1WheelsOff") as agg_0, SUM("On_Time_On_Time_Performance_2016_1"."CRSElapsedTime" + "On_Time_On_Time_Performance_2016_1"."NASDelay") as agg_1, COUNT(*) as agg_2 FROM "L_AIRLINE_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRLINE_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."AirlineID" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."Div1AirportSeqID" = "L_AIRPORT_SEQ_ID"."Code" LEFT OUTER JOIN "L_CANCELLATION" ON "On_Time_On_Time_Performance_2016_1"."CancellationCode" = "L_CANCELLATION"."Code"  WHERE "L_AIRLINE_ID"."Description" LIKE '%Flyi%ng%' AND "L_AIRPORT_SEQ_ID"."Description" IN ('Canon City, CO: Fremont County') AND "On_Time_On_Time_Performance_2016_1"."OriginState" = 'PR' AND "L_AIRPORT_SEQ_ID"."Code" >= 5701431 AND "L_CANCELLATION"."Code" = 2 AND ("L_CANCELLATION"."Description" LIKE '%National%' OR "L_CANCELLATION"."Description" LIKE '%Ca%rrier%')