SELECT MAX("On_Time_On_Time_Performance_2016_1"."OriginAirportSeqID") as agg_0, SUM("On_Time_On_Time_Performance_2016_1"."DepTime") as agg_1, COUNT(*) as agg_2 FROM "L_AIRPORT_SEQ_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRPORT_SEQ_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."Div2AirportSeqID" LEFT OUTER JOIN "L_ONTIME_DELAY_GROUPS" ON "On_Time_On_Time_Performance_2016_1"."DepartureDelayGroups" = "L_ONTIME_DELAY_GROUPS"."Code" LEFT OUTER JOIN "L_DISTANCE_GROUP_250" ON "On_Time_On_Time_Performance_2016_1"."DistanceGroup" = "L_DISTANCE_GROUP_250"."Code"  WHERE "L_AIRPORT_SEQ_ID"."Description" IN ('Canon City, CO: Fremont County', 'Hudiksvall, Sweden: Hudiksvall Airport', 'Akiachak, AK: Akiachak Airport', 'North Adams, MA: Harriman-and-West', 'Cotulla, TX: Cotulla-La Salle County', 'Chiayi, Taiwan: Chiayi Airport', 'Seletar, Singapore: Seletar Airport', 'Thandwe, Burma: Thandwe Airport') AND ("On_Time_On_Time_Performance_2016_1"."Carrier" NOT LIKE '%AS%' OR ("On_Time_On_Time_Performance_2016_1"."Dest" IN ('LAS', 'PHX', 'SFO', 'LAX', 'MCO', 'IAH', 'ATL', 'DEN', 'DFW') AND "On_Time_On_Time_Performance_2016_1"."Carrier" = 'HA')) AND "L_DISTANCE_GROUP_250"."Description" IN ('750-999 Miles', '1250-1499 Miles', '1500-1749 Miles', '1000-1249 Miles') AND "On_Time_On_Time_Performance_2016_1"."OriginCityName" IN ('New York, NY', 'Atlanta, GA', 'Chica, IL', 'Phoenix, AZ', 'Los Angeles, CA') AND "L_DISTANCE_GROUP_250"."Code" >= 34 AND "L_ONTIME_DELAY_GROUPS"."Description" LIKE '%15%0%'